INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(37195, 1, 0, 'Get our men in position immediately, Tobias.  We cannot let the Scythe fall into the hands of the Forsaken!!', 12, 0, 100, 53, 0, 0, 'Lord Darius Crowley'),
(37195, 2, 0, 'Lorna!', 14, 0, 100, 0, 0, 19511, 'Lord Darius Crowley'),
(37195, 3, 0, 'Does this toad speak for you, Genn?  Do you come to our dwelling as a friend?  Or do you come as a tyrant?', 14, 0, 100, 0, 0, 19510, 'Lord Darius Crowley'),
(37195, 4, 0, 'Aye, Genn.  It is not law that binds us.  It is something far stronger.  My men are ready to give their lives under your command.', 12, 0, 100, 0, 0, 19512, 'Lord Darius Crowley'),
(38051, 1, 0, 'Darius!  The Dark Rangers have the Scythe!  They got to it before we were able to reach it.', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle'),
(38051, 2, 0, 'It will be done!', 12, 0, 100, 0, 0, 0, 'Tobias Mistmantle'),
(38029, 1, 0, 'Keep them occupied, my brothers!  Allow $N to retrieve the scythe!', 14, 0, 100, 0, 0, 0, 'Tobias Mistmantle'),
(38029, 2, 0, 'Fall back!  Retreat into the woods!', 14, 0, 100, 0, 0, 0, 'Tobias Mistmantle'),
(37870, 1, 0, 'Just as Goldrinn''s spirit once blessed our druids, let $N be blessed with the wisdom of his race and the ferocity of the wolf god.', 12, 0, 100, 1, 0, 0, 'Lyros Swiftwind'),
(37873, 1, 0, 'Just as Daral''nir soothes the cursed druids who gave into the beast and abandoned balance, let Tal''doren soothe $N.', 12, 0, 100, 1, 0, 0, 'Vassandra Stormclaw'),
(36814, 1, 0, 'Let the Scythe unbind that which was not meant to be bound!  Let the soul master the beast, lest the beast master the soul!', 12, 0, 100, 1, 0, 0, 'Talran of the Wild'),
(38768, 1, 0, 'Father!', 14, 0, 100, 0, 0, 19697, 'Lorna Crowley'),
(38766, 1, 0, 'Crowley!  You and your elven allies are hereby ordered to serve along the king''s army.  Cursed or not, you are still bound by Gilnean law!', 12, 0, 100, 25, 0, 19636, 'Lord Godfrey'),
(38766, 2, 0, 'Impossible!', 14, 0, 100, 0, 0, 19637, 'Lord Godfrey'),
(38767, 1, 0, 'No, old friend.  I''ve come to you as an equal.', 12, 0, 100, 0, 0, 19723, 'King Genn Greymane'),
(38767, 2, 0, 'It is decided, then.  We will unite all Gilneans and drive the Forsaken from our lands.', 12, 0, 100, 0, 0, 19724, 'King Genn Greymane');
